mapper.select:
SELECT COUNT(*) AS CNT
  FROM ITSM_COM_USER_INFO
 WHERE LOGIN_ID = #{p_login_id}

mapper.insert:
	INSERT
	  INTO ITSM_COM_USER_INFO(
	  	   SEQ_ID
  		 , LOGIN_ID 
	     , PASSWORD 
	     , NAME 
	     , E_MAIL 
	     , COMPANY_NAME 
	     , DEPARTMENT 
	     , RANK 
	     , BUSINESS_ROLE 
	     , HOME_ADDRESS
	     , PHONE1 
	     , TEL 
	     , STATUS 
	     , QUESTION
	     , ANSWER
	     , CREATE_DATE 
	     , CREATED_BY 
	     , LAST_UPDATE_DATE 
	     , LAST_UPDATE_BY 
		 )
	VALUES(
		   #{seq_id}
		 , #{p_login_id}
	     , #{p_password}
	     , #{p_name}
	     , #{p_e_mail}
	     , #{p_company_name}
	     , #{p_department}
	     , #{p_sel_position}
	     , #{p_business_role}
	     , #{p_home_address}
	     , #{p_phone1}
	     , #{p_tel}
	     , 'Y'
	     , #{p_sel_question}
	     , #{p_answer}
	     , TO_CHAR(SYSDATE, 'YYYY-MM-DD')
	     , #{p_login_id}
	     , TO_CHAR(SYSDATE, 'YYYY-MM-DD')
	     , #{p_login_id}
	     )
