mapper.select:
SELECT T2.* FROM (SELECT T1.* , FLOOR((ROWNUM - 1) / #{pageSize} + 1 ) AS PAGE FROM (SELECT SEQ_ID ,LOGIN_ID ,PASSWORD ,NAME ,E_MAIL ,COMPANY_NAME ,DEPARTMENT ,RANK AS RANK_CODE ,NVL2(RANK ,(SELECT ICCID.MEDIUM_CODE_KOR_NAME FROM ITSM_COM_CODE_INFO_DETAIL ICCID WHERE ICCID.MEDIUM_CODE = RANK AND ROWNUM = 1) ,'') AS RANK ,PHONE1 ,PHONE2 ,TEL ,HOME_ADDRESS ,BUSINESS_ROLE ,ERP_LEGACY_GUBUN ,SM_MAN_FLAG ,SM_MAN_FLAG AS SM_MAN_FLAG_CHANGED ,STATUS ,STATUS AS STATUS_CHANGED ,QUESTION AS QUESTION_CODE ,NVL2(RANK ,(SELECT ICCID.MEDIUM_CODE_KOR_NAME FROM ITSM_COM_CODE_INFO_DETAIL ICCID WHERE ICCID.MEDIUM_CODE = QUESTION AND ROWNUM = 1) ,'') AS QUESTION ,ANSWER ,CREATE_DATE ,CREATED_BY ,LAST_UPDATE_DATE ,LAST_UPDATE_BY FROM ITSM_COM_USER_INFO WHERE 1=1
AND LOGIN_ID LIKE '%' || #{p_login_id} || '%'
AND NAME LIKE '%' || #{p_name} || '%'
AND STATUS = #{p_sel_status}
ORDER BY NAME ) T1 ) T2 WHERE PAGE = #{curPageNo}
